sources.styles/blocks/input.scss:
.input {
  padding: 14px 16px;
  background: $input-bg-color;
  border-radius: 40px;
  border: 1px solid $input-bg-color;
  width: 100%;
  height: 48px;

  &::placeholder {
    font-family: inherit;
    font-size: 14px;
    line-height: 20px;
    color: $input-placeholder-color;
    outline: none;
  }

  &--usermessage {
    font-family: inherit;
    font-size: 14px;
    line-height: 20px;
    height: 148px;
    border-radius: 16px;
    margin: 0 0 32px;
    resize: none;
  }

  &:hover {
    color: $text-color-dark;
    border: 1px solid $input-hover-border-color;

    &::placeholder{
      color: $text-color-dark;
    }
  }

  &:focus {
    border: 1px solid $text-color-dark;
    outline: none;
  }
}

sources.styles/blocks/button.scss:
.button {
  line-height: 27px;
  font-weight: 700;
  padding: 14px 0;
  margin: 0;
  text-align: center;
  color: $color-white;

  background-color: $text-color-dark;
  border: 3px solid $text-color-dark;
  border-radius: 40px;
  cursor: pointer;
  transition-duration: $transition-duration;

  &:hover {
    background-color: $color-white;
    border: 3px solid $text-color-dark;
    color: $text-color-dark;
  }

  &:active {
    background-color: $button-active-bg-color;
    color: $color-white;
  }

  &--view-all {
    width: 100%;
    grid-column: 1 / -1;

    @include onTablet {
      grid-column: 2 / -2;
    }

    @include onDesktop {
      grid-column: 5 / 9;
    }
  }

  &--send {
    margin: 0 0 30px;
    width: 100%;
  }
}

